String.prototype.trim = function (charlist) {
    charlist = charlist || ' \t\n\r\x0B';
    return this.replace(new RegExp('(^[' + charlist + ']+)|([' + charlist + ']+$)', 'g'), '');
}

String.prototype.ltrim = function (charlist) {
    charlist = charlist || ' \t\n\r\x0B';
    return this.replace(new RegExp('^[' + charlist + ']+', 'g'), '');
}

String.prototype.rtrim = function (charlist) {
    charlist = charlist || ' \t\n\r\x0B';
    return this.replace(new RegExp('[' + charlist + ']+$', 'g'), '');
}

function getChapterImage(filename) {
        //var filename = chapterImages[page - 1];
        var host;
        if (typeof chapterImageHost !== "undefined"){
            host = chapterImageHost;
        }else {
             host = "https://img01.eshanyao.com";
            //host = getResHostDomain().trim('/');
        }
        if (filename === undefined) return host + '/images/default/common.png';
        if (filename.match(/^https?:\/\/(images.dmzj.com|imgsmall.dmzj.com)/i)) return 'https://img01.eshanyao.com/showImage.php?url='+encodeURI(filename);
        if (filename.match(/^[a-z]\//i)) return 'https://img01.eshanyao.com/showImage.php?url='+encodeURI("https://images.dmzj.com/"+filename);
        if (filename.match(/^https?:\/\/(manhua.qpic.cn)/i)) return 'https://dl.manhuachi.com/acqq.php?url='+encodeURI(filename);
        if (filename.match(/^(http:|https:|ftp:|^)\/\//i)) return filename;
        filename = chapterPath.trim('/') + '/' + filename.ltrim('/');
        return host + '/' + filename.ltrim('/');
}

