ruleSearch.bookList:
tr!0||class.line
<js>
result.toArray().slice(0,8).map(o=>{
    if(!baseUrl.includes('top')){
    var detail = o.select('td:eq(0) a').attr('href');
   }else {
      var detail = String(o.select('a:eq(1)').attr('href')).replace(/^\//, 'https://www.meiguixsw.com/');
    }
    try{var J = org.jsoup.Jsoup.parse(java.ajax(detail));}
    catch(_){return {}}
    var oText = s => String(o.select(s).text()).trim();
    var jText = s => String(J.select(s).text()).trim();
    return {
       name:  oText('td:eq(0)').replace(/\[.*\]/g,'')||oText('a:eq(1)').replace(/\[.*\]/g,''),
       author: oText('td:eq(2)')||oText('a:eq(2)'),
       tags:  J.select('.pd_r:eq(0)').text() + ',' + jText('.pd_r:eq(5)'),
       num: jText('.pd_r:eq(4)') + '字',
       latest: !baseUrl.includes('top')? (oText('td:eq(1)').replace(/[\(（].*[求更谢乐发推].*[）\)]/,'').replace(/(正文)?(\s+|_)/,'') + '(' + oText('td:eq(4)')+')'): jText('.new_t').replace('最后更新：',''),
       intro: jText('#bookintro').replace(/<.*>/,''),
       cover: J.select('#bookimg img').attr('src'),
       catalogUrl: String(J.select('#newlist a').attr('href')).replace(/^\//, 'https://m.meiguixsw.com/')
    }
});
</js>

.grid@tr!0@js:result.toArray().slice(0,5).map(o=>{
    var detail = String(o.select('a:eq(0) ').attr('href')).replace(/^\//, baseUrl.match(/(.+?\/\/[^\/]+\/)/)[1]);
   try{
    try{var J = org.jsoup.Jsoup.connect(detail).get();
    }catch(_){return {}}
 }catch(_){return {}}
    var oText = s=>String(o.select(s).text().trim());
    var jText = s=>String(J.select(s).text().trim()).replace(/.+：/, '');
    return {
       name:  oText('td:eq(0)'),
       author:oText('td:eq(2)'),
       tags:  [jText('.bq span:eq(2)'),jText('.bq span:eq(3)')].join(','),
       latest:oText('td:eq(1)').replace(/[\(（【].*/,'').replace(/.*(第[^章]+章)/,'$1') + oText('td:eq(4)').replace(/(\d+-\d+-\d+)/,'（$1）'),
       intro: jText('.jj'),
       cover: J.select('.pic img').attr('src'),
       catalogUrl:detail
    }
}).filter(v =>v.name);

